mapper.update:
update dish set name =
    #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description},
             status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select d.*,c.name from dish d left join category c on d.category_id = c.id


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
